sys_script_include.script:
var ValidateWhenNeeded = Class.create();
ValidateWhenNeeded.prototype = {
		initialize : function() {
		},
		whenNeededIsValid: function(when_needed) {
	// rightnow stores the current time
		var rightnow = new GlideDateTime();
		// Create a GlideDateTime object for the When needed date
		var whenNeeded = new GlideDateTime(when_needed);

		// If the When needed date is before rightnow, do not write the record to the database
		// Output an error message to the screen
		var daynow = rightnow.getLocalDate();
		var daywhenNeeded = whenNeeded.getLocalDate();
		var check = whenNeeded.before(rightnow) | daynow.getValue() == daywhenNeeded.getValue();
		return check;
	},

    type: 'ValidateWhenNeeded'
};
